import React, { useState } from "react";
import { Navigate } from "react-router-dom";

export default function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const loginCredentials = {
    admin_user: {
      email: "jawad.asghar@outlook.com",
      password: "Abcd@1234",
    },
    normal_user: {
      email: "hassan.butt@gmail.com",
      password: "Abcd@1234",
    },
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Validate form fields
    if (!username || !password) {
      return alert("Please enter a username and password.");
    }
    const credentials_check = loginCredentials.admin_user.map(
      (e) => e.email
    );
    console.log(JSON.stringify(credentials_check));
    // console.log(JSON.stringify(loginCredentials[0].admin_user.email));
    setIsLoggedIn(true);
  };

  if (isLoggedIn) {
    return <Navigate to="/admin-dashboard" />;
  }

  return (
    <form
      onSubmit={handleSubmit}
      className="relative flex flex-col justify-center min-h-screen overflow-hidden"
    >
      <div className="w-full p-6 m-auto bg-white rounded-md shadow-xl shadow-rose-300/40 ring ring-2 ring-gray-600 lg:max-w-xl">
        <h1 className="text-3xl font-semibold text-center text-gray-700 uppercase">
          Sign in
        </h1>
        <div className="mt-6">
          <div className="mb-2">
            <label
              htmlFor="email"
              className="block text-sm font-semibold text-gray-800"
            >
              Email
            </label>
            <input
              type="email"
              id="username"
              value={username}
              onChange={(event) => setUsername(event.target.value)}
              className="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border rounded-md focus:border-gray-400 focus:ring-gray-300 focus:outline-none focus:ring focus:ring-opacity-40"
            />
          </div>
          <div className="mb-2">
            <label
              htmlFor="password"
              className="block text-sm font-semibold text-gray-800"
            >
              Password
            </label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(event) => setPassword(event.target.value)}
              className="block w-full px-4 py-2 mt-2 text-gray-700 bg-white border rounded-md focus:border-gray-400 focus:ring-gray-300 focus:outline-none focus:ring focus:ring-opacity-40"
            />
          </div>
          <a href="#" className="text-xs text-gray-600 hover:underline">
            Forget Password?
          </a>
          <div className="mt-6">
            <button
              type="submit"
              className="w-full px-4 py-2 tracking-wide text-white transition-colors duration-200 transform bg-gray-700 rounded-md hover:bg-gray-600 focus:outline-none focus:bg-gray-600"
            >
              Login
            </button>
          </div>
        </div>

        <p className="mt-8 text-xs font-light text-center text-gray-700">
          {" "}
          Don't have an account?{" "}
          <a href="#" className="font-medium text-gray-600 hover:underline">
            Sign up
          </a>
        </p>
      </div>
    </form>
  );
}
